export default function App() {
    return (
      <>
        <Title />
  
        <div className="enterfoods">
          <div id="shiftenterfoods">
            <h3 id="addFoodsConsumed">Add Foods Consumed</h3>
        <form>
          <Input Placeholder="Enter Foods" ID="foodsInputBox"></Input>
        </form>
      </div>
      <div id="submitFood">
        <Button text="Submit Food"></Button>
         </div>
         <div id="entermacro">
            <h3>Enter Your Macronutrient Goals Below (grams)</h3>
            <form>
              <label htmlFor="protein">Protein:</label>
              <Input ID="protein"></Input>
             <label className="entermacro" htmlFor="carbs">
                Carbohydrates:
              </label>
              <Input ID="carbs"></Input>
              <label className="entermacro" htmlFor="fats">
                Fats:
             </label>
             <Input ID="fats"></Input>
            </form>
          </div>
        </div>
    
       <FoodTable />
  
        <NutrientTotals />
  
        <p id="note" style={{ paddingLeft: 700, visibility: "hidden" }}>
          <b>Note: all quantities displayed are in grams</b>
        </p>
      </>
    );
}
  
function Title() {
    return <h1 style={{ textAlign: "center", fontSize: "400%" }}>Diet Tracker v1</h1>;
}
  
function Input({Placeholder, ID}) {
    return (
          <input type="text" placeholder={Placeholder} id={ID}/>
    );
}
  
function NutrientTotals() {
    return (
      <div id="nutrienttotal">
      <p style={{ paddingLeft: 700 }}><b>Totals:</b></p>
      <p id="calorietotal" style={{ paddingLeft: 40 }} />
      <p id="proteintotal" style={{ paddingLeft: 50 }} />
      <p id="carbtotal" style={{ paddingLeft: 50 }} />
      <p id="fattotal" style={{ paddingLeft: 50 }} />
    </div>
    );
}
  
function Button({text}) {
    function clicked() {
      alert("button clicked");
    }
  
    return <button onClick={clicked}>{text}</button>;
}
  
